#! python3

from Crypto.Util.number import *
from secrets import n, m, flag

assert isPrime(n**11 + 2022) and isPrime(m**11 + 2022)

p, q = n**11 + 2022, m**11 + 2022

N = p*q
e = 65537

m = bytes_to_long(flag)
c = pow(m, e, N)

print(f"{N = }")
print(f"{e = }")
print(f"{c = }")

